import React from "react";

type Props = {
  tags: string[];
  selectTag: (tag: string) => void;
  selectedTag: string;
}

export const Sidebar: React.FC<Props> = ({ tags, selectTag, selectedTag }) => {
  return (
    <div className="sidebar">
      <ul>
        <li
          className={selectedTag === "" ? "selected" : ""}
          onClick={() => selectTag("")}
        >
          All Courses
        </li>
        {tags.map((tag) => (
          <li
            key={tag}
            className={selectedTag === tag ? "selected" : ""}
            onClick={() => selectTag(tag)}
          >
            {tag}
          </li>
        ))}
      </ul>
    </div>
  );
};
