import React from 'react';
import { Course } from '../types/Course';


type Props = {
  course: Course;
}

export const CourseCard: React.FC<Props> = ({ course }) => (
    <div className="course-card" style={{ backgroundColor: course.bgColor }}>
      <img src={course.image} alt={course.name} style={{ width: '100%', height: 'auto' }} />
      <h3>{course.name}</h3>
      <div>{course.tags.join(', ')}</div>
    </div>
  );
